    public function getSourceContext()
    {
        return new Twig_Source("{% extends 'base.html.twig' %}

{% block title %}Games index{% endblock %}

{% block body %}
    <h1>Games index</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Name</th>
                <th>Description</th>
                <th>Duration</th>
                <th>PlayerMin</th>
                <th>PlayerMax</th>
                <th>Picture</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for game in games %}
            <tr>
                <td>{{ game.id }}</td>
                <td>{{ game.name }}</td>
                <td>{{ game.description }}</td>
                <td>{{ game.duration }} min</td>
                <td>{{ game.playerMin }}</td>
                <td>{{ game.playerMax }}</td>
                <td><img src=\"/images/{{ game.picture }}\" width=\"100px\"/></td>
                <td>
                    <button class=\"btn btn-info\"><a href=\"{{ path('games_show', {'id': game.id}) }}\">Afficher</a></button>
                    <button class=\"btn btn-warning\"><a href=\"{{ path('games_edit', {'id': game.id}) }}\">Modifier</a></button>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"8\">Aucun jeux dans la base</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <button class=\"btn btn-secondary\"><a href=\"{{ path('games_new') }}\">Créer un nouveau jeu</a></button>
{% endblock %}
", "games/index.html.twig", "/home/paw/coding/rendus/Projet_Fin_Formation/projectNetEscape/netEscape/templates/games/index.html.twig");
